import React, { useState, useEffect } from 'react';
import Container from '@material-ui/core/Container';
import CssBaseline from '@material-ui/core/CssBaseline';
import Table from '../../components/Table';
import { getMovies, deleteMovie } from '../../services/Movies/movieService';
import useStyles from './styles';

export default function Movies() {
  const classes = useStyles();
  const [selected, setSelected] = useState([]);
  const [rows, setRows] = useState([]);

  const headCells = [
    {
      id: 'name',
      numeric: false,
      disablePadding: true,
      label: 'Nome',
    },
    {
      id: 'genre',
      numeric: false,
      disablePadding: true,
      label: 'Gênero',
    },
    {
      id: 'creationDate',
      numeric: false,
      disablePadding: true,
      label: 'Data Criação',
    },
    { id: 'status', numeric: false, disablePadding: true, label: 'Status' },
  ];

  async function loadMovies() {
    const { data } = await getMovies();
    const movies = data.map(m => {
      return {
        id: m.id,
        name: m.name,
        genre: m.genre.name,
        creationDate: new Date(m.creationDate).toLocaleDateString(),
        status: m.status === 1 ? 'Ativo' : 'Inativo',
      };
    });
    setRows(movies);
  }

  useEffect(() => {
    loadMovies();
  }, []);

  const handleAdd = () => {
    console.log('Add');
  };

  const handleEdit = () => {
    const movieId = selected[0];
    console.log('Edit', movieId);
  };

  const handleDelete = async () => {
    if (selected.length > 1) {
      console.log('Delete multiple', selected);
    } else {
      const movieId = selected[0];
      await deleteMovie(movieId);
    }
    await loadMovies();
  };

  return (
    <Container component="main">
      <CssBaseline />
      <div className={classes.paper}>
        <Table
          title="Filmes"
          headCells={headCells}
          rows={rows}
          selected={selected}
          setSelected={setSelected}
          defaultSortedProperty="name"
          onAdd={handleAdd}
          onEdit={handleEdit}
          onDelete={handleDelete}
        />
      </div>
    </Container>
  );
}
