import React, { useState, useEffect } from 'react';
import Container from '@material-ui/core/Container';
import CssBaseline from '@material-ui/core/CssBaseline';
import Table from '../../components/Table';
import { getGenres, deleteGenre } from '../../services/Genres/genreService';
import useStyles from './styles';

export default function Genres() {
  const classes = useStyles();
  const [selected, setSelected] = useState([]);
  const [rows, setRows] = useState([]);

  // function createFakeData(id, name, creationDate, status) {
  //   return { id, name, creationDate, status };
  // }

  // const fakeRows = [
  //   createFakeData(1, 'Ação', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Romance', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Ficção Científica', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Terror', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Aventura', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Drama', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Comédia', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Animação', '22/03/2020', 'Ativo'),
  //   createFakeData(1, 'Policial', '22/03/2020', 'Ativo'),
  // ];

  const headCells = [
    {
      id: 'name',
      numeric: false,
      disablePadding: true,
      label: 'Nome',
    },
    {
      id: 'creationDate',
      numeric: false,
      disablePadding: true,
      label: 'Data Criação',
    },
    { id: 'status', numeric: false, disablePadding: true, label: 'Status' },
  ];

  useEffect(() => {
    async function loadGenres() {
      const { data } = await getGenres();
      setRows(data);
    }
    loadGenres();
  }, [rows]);

  const handleAdd = () => {
    console.log('Add');
  };

  const handleEdit = () => {
    const genre = selected[0];
    console.log('Edit', genre);
  };

  const handleDelete = async () => {
    if (selected.length > 1) {
      console.log('Delete multiple', selected);
    } else {
      const genre = selected[0];
      await deleteGenre(genre);
    }
    await loadGenres();
  };

  return (
    <Container component="main">
      <CssBaseline />
      <div className={classes.paper}>
        <Table
          title="Gêneros"
          headCells={headCells}
          rows={rows}
          selected={selected}
          setSelected={setSelected}
          defaultSortedProperty="name"
          onAdd={handleAdd}
          onEdit={handleEdit}
          onDelete={handleDelete}
        />
      </div>
    </Container>
  );
}
