/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable react/forbid-prop-types */
import React, { useRef, useEffect, useState } from 'react';
import { Form } from '@unform/web';
import * as Yup from 'yup';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import { TextField, Checkbox } from 'unform-material-ui';
import PropTypes from 'prop-types';
import { getGenre, saveGenre } from '../../services/Genres/genreService';
import useStyles from './styles';

export default function GenreForm({ history, match }) {
  const classes = useStyles();

  const formRef = useRef(null);

  const [genre, setGenre] = useState({
    id: 0,
    name: '',
    status: true,
    creationDate: new Date().toISOString(),
  });

  const schema = Yup.object().shape({
    name: Yup.string().required('Nome é obrigatório!'),
  });

  const handleSubmit = async data => {
    try {
      // Remove all previous errors
      formRef.current.setErrors({});
      await schema.validate(data, {
        abortEarly: false,
      });
      // Validation passed
      const genreEntity = { status: data.status ? 1 : 0, ...data };
      await saveGenre(genreEntity);
      history.push('/genres');
    } catch (err) {
      const validationErrors = {};
      if (err instanceof Yup.ValidationError) {
        err.inner.forEach(error => {
          validationErrors[error.path] = error.message;
        });
        formRef.current.setErrors(validationErrors);
      }
    }
  };

  const loadGenre = async id => {
    if (id > 0) {
      const { data } = await getGenre(id);
      const { status, ...model } = data;
      const genreModel = {
        status: data.status === 1,
        ...model,
      };
      formRef.current.setData(genreModel);
    }
  };

  useEffect(() => {
    const { id } = match.params;
    loadGenre(id);
  }, []);

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5">
          Gênero
        </Typography>
        <Form
          ref={formRef}
          onSubmit={handleSubmit}
          className={classes.form}
          noValidate
        >
          <TextField
            name="name"
            variant="outlined"
            margin="normal"
            fullWidth
            label="Nome"
            autoComplete="name"
            autoFocus
          />
          <FormControlLabel
            control={<Checkbox name="status" value="status" color="primary" />}
            label="Ativo"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Salvar
          </Button>
        </Form>
      </div>
    </Container>
  );
}

GenreForm.defaultProps = {
  history: null,
  match: null,
};

GenreForm.propTypes = {
  history: PropTypes.any,
  match: PropTypes.any,
};
