/* eslint-disable react/forbid-prop-types */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import Container from '@material-ui/core/Container';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Link from '@material-ui/core/Link';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import PropTypes from 'prop-types';
import Joi from 'joi-browser';
import { login, getCurrentUser } from '../../services/Auth/authService';
import useStyles from './styles';

export default function Auth({ history, location }) {
  const [data, setData] = useState([]);
  const [errors, setErrors] = useState();

  const classes = useStyles();

  const handleRegistration = () => {
    history.push('/registration');
  };

  const schema = {
    username: Joi.string()
      .required()
      .label('Email'),
    password: Joi.string()
      .required()
      .label('Senha'),
  };

  const doSubmit = async () => {
    try {
      await login(data.username, data.password);
      const { state } = location;
      window.location = state ? state.from.pathname : '/';
    } catch (ex) {
      if (ex.response && ex.response.status === 400) {
        const newErrors = { ...errors };
        newErrors.username = ex.response.data;
        setErrors(newErrors);
      }
    }
  };

  useEffect(() => {
    async function loadUser() {
      if (await getCurrentUser()) {
        history.push('/');
      }
    }

    loadUser();
  });

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Login
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Senha"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Lembrar"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Entrar
          </Button>
          <Grid container>
            <Grid item xs>
              {/* <Link href="#" variant="body2">
                Esqueceu a senha?
              </Link> */}
            </Grid>
            <Grid item>
              <Link variant="body2" onClick={handleRegistration}>
                NÃ£o tem cadastro? Registre-se
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
}

Auth.defaultProps = {
  history: null,
};

Auth.propTypes = {
  history: PropTypes.any,
};
