import React, { useRef } from 'react';
import { Form } from '@unform/web';
import * as Yup from 'yup';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { TextField, Checkbox } from 'unform-material-ui';
import useStyles from './styles';

export default function Auth() {
  const classes = useStyles();

  const formRef = useRef(null);

  const schema = Yup.object().shape({
    email: Yup.string()
      .email('Email inválido!')
      .required('Email é obrigatório!'),
    password: Yup.string().required('Senha é obrigatória!'),
  });

  const handleSubmit = async data => {
    try {
      console.log(data);
      // Remove all previous errors
      formRef.current.setErrors({});
      await schema.validate(data, {
        abortEarly: false,
      });
      // Validation passed
    } catch (err) {
      const validationErrors = {};
      if (err instanceof Yup.ValidationError) {
        err.inner.forEach(error => {
          validationErrors[error.path] = error.message;
        });
        formRef.current.setErrors(validationErrors);
      }
    }
  };

  const validateProperty = async ({ name, value }) => {
    const error = await Yup.reach(schema, name).validate(value);

    return error || null;
  };

  const handleChange = async ({ currentTarget: input }) => {
    const error = await validateProperty(input.name, input.value);
    console.log(error);
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Login
        </Typography>
        <Form
          ref={formRef}
          onSubmit={handleSubmit}
          className={classes.form}
          noValidate
        >
          <TextField
            name="email"
            type="email"
            variant="outlined"
            margin="normal"
            fullWidth
            label="Email"
            autoComplete="email"
            autoFocus
            onChange={handleChange}
          />
          <TextField
            name="password"
            type="password"
            variant="outlined"
            margin="normal"
            fullWidth
            label="Senha"
            autoComplete="current-password"
            autoFocus
          />
          <FormControlLabel
            control={
              <Checkbox name="remember" value="remember" color="primary" />
            }
            label="Lembrar"
          />
          {/* <button type="submit">Sign in</button> */}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Entrar
          </Button>
        </Form>
      </div>
    </Container>
  );
}
