/* eslint-disable no-restricted-syntax */
/* eslint-disable react/forbid-prop-types */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import Container from '@material-ui/core/Container';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Link from '@material-ui/core/Link';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import PropTypes from 'prop-types';
import Joi from 'joi-browser';
import { login, getCurrentUser } from '../../services/Auth/authService';
import useStyles from './styles';

export default function Auth({ history, location }) {
  const [data, setData] = useState([]);
  const [errors, setErrors] = useState();

  const classes = useStyles();

  const handleRegistration = () => {
    history.push('/registration');
  };

  const schema = {
    email: Joi.string()
      .required()
      .label('Email'),
    password: Joi.string()
      .required()
      .label('Senha'),
  };

  const validate = () => {
    const options = { abortEarly: false };
    console.log('data', data);
    const { error } = Joi.validate(data, schema, options);
    console.log('error', error);

    if (!error) return null;
    const newErrors = {};

    for (const item of error.details) newErrors[item.path[0]] = item.message;

    return newErrors;
  };

  const doSubmit = async e => {
    e.preventDefault();
    const newErrors = validate();
    console.log(newErrors);
    setErrors(newErrors || {});
    if (newErrors) return;

    try {
      await login(data.username, data.password);
      const { state } = location;
      window.location = state ? state.from.pathname : '/';
    } catch (ex) {
      if (ex.response && ex.response.status === 400) {
        newErrors.username = ex.response.data;
        setErrors(newErrors);
      }
    }
  };

  const validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const propSchema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, propSchema);
    return error ? error.details[0].message : null;
  };

  const handleChange = ({ currentTarget: input }) => {
    const newErrors = { ...errors };
    const errorMessage = validateProperty(input);
    if (errorMessage) newErrors[input.name] = errorMessage;
    else delete newErrors[input.name];

    const newData = { ...data };
    newData[input.name] = input.value;
    setData(newData);
    setErrors(newErrors);
  };

  useEffect(() => {
    async function loadUser() {
      if (await getCurrentUser()) {
        history.push('/');
      }
    }

    loadUser();
  });

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Login
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            error={errors && errors.email}
            helperText={
              errors && errors.email ? 'Email deve ser informado!' : ''
            }
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            autoFocus
            onChange={handleChange}
          />
          <TextField
            error={errors && errors.password}
            helperText={
              errors && errors.password ? 'Senha deve ser informada!' : ''
            }
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Senha"
            type="password"
            id="password"
            autoComplete="current-password"
            onChange={handleChange}
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Lembrar"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={doSubmit}
          >
            Entrar
          </Button>
          <Grid container>
            <Grid item xs>
              {/* <Link href="#" variant="body2">
                Esqueceu a senha?
              </Link> */}
            </Grid>
            <Grid item>
              <Link variant="body2" onClick={handleRegistration}>
                Não tem cadastro? Registre-se
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
}

Auth.defaultProps = {
  history: null,
  location: null,
};

Auth.propTypes = {
  history: PropTypes.any,
  location: PropTypes.any,
};
